window.onload=init;var init=function(){var e=document.getElementsByClassName("demo2"),n=document.getElementsByClassName("demo2__controls--play"),t=document.getElementsByClassName("demo2__controls--mute");n.addEventListener("click",function(t){t.preventDefault(),e.paused?(e.play(),n.innerHTML="Pause"):(e.pause(),n.innerHTML="Play")}),t.addEventListener("click",function(n){n.preventDefault(),e.muted?(e.muted=!1,t.innerHTML="Mute"):(e.muted=!0,t.innerHTML="Unmute")})};